targets.query:
from(bucket:"labBucket")
  |> range(start: -2m)
  |> filter(fn: (r) =>
    r._measurement == "system" and
    r._field == "uptime"
  )

from(bucket:"labBucket")
    |> range(start: -3m)
    |> filter(fn: (r) =>
        r._measurement == "temp" and
        r._field == "temp"        
        )
